INSERT INTO `sitio_company` VALUES (1,'FBC -  Financial Bureau Consulting','Fernando Amaral. Formado em Administração de Empresas pela Universidade Federal do Rio Grande do Norte, MBA em Gestão Financeira, Controladoria e Auditoria pela FGV, especializei-me em recuperação extrajudicial e judicial de micro e pequenas empresas com dificuldades financeiras, aumento de market share, otimização logística, rotinas administrativas, best market pratices, controladoria e auditoria. Fui professor de Finanças na Universidade Federal do Rio Grande do Norte nas disciplinas Administração Financeira, Matemática Financeira, Administração Financeira, Orçamento empresarial, Oficina de Projetos I e II, Jogos de Negócios. Perito financeiro pelo estado do Rio Grande do Norte. 19 anos de experiência em finanças corporativas. Professor de Finanças na FGV.','img/about/bovespa.jpg','Em breve','Em breve','Natal','fernando@financialbureauconsulting.com.br','2016-03-27 20:37:48','+55 (84) 99985-5988','Em breve');
